import {createSlice} from "@reduxjs/toolkit";

const cartSlice = createSlice({
    name: "cart",
    initialState: {
        items:[],
        totalQuantity: 0,
    },
    reducers: {
        addItemToCart: (state, action) => {
            const newItem = action.payload;
            const existngItem = state.items.find(item => item.id === newItem.id);
            state.totalQuantity ++;
            if (!existngItem) {
                state.items.push({
                    itemId:newItem.id,
                    price:newItem.price,
                    quantity:1,
                    totalPrice:newItem.price,
                    name: newItem.title
                });
            }else {
                existngItem.quantity++;
                existngItem.totalPrice = existngItem.totalPrice + newItem.price;
            }
        },
        removeItemFromCart(state, action) {
            const id = action.payload;
            const existngItem = state.items.find(item => item.id === id);
            if (existngItem.quantity === 1) {
                state.items = state.items.filter(item => item.id !== id);
            }else {
                existngItem.quantity--;
            }
        }
    }
})
export const cartActions = cartSlice.actions;
export default cartSlice
